//   LaptopOutlined,
//   DatabaseOutlined,
//   MenuUnfoldOutlined,
//   MenuFoldOutlined,
// } from "@ant-design/icons";

// const { Header, Sider } = Layout;
// const { SubMenu } = Menu;

const siteTitle = "Youtube Recommender";

export default function StandardLayout({ title, header, subheader, children }) {
  return (
    <div>
      <Head>
        <title>{title ? title : siteTitle}</title>
      </Head>
      <Layout
        className="p-3"
        style={{
          padding: "1rem",
          overflowY: "scroll",
          paddingBottom: "2rem",
        }}
      >
        {children}
      </Layout>
    </div>
  );
}
