import { useState, useEffect, useRef } from "react";
import { Button, Card, Image, Tag, Input } from "antd";
import { PlusOutlined } from "@ant-design/icons";
import youtubeDataService from "../services/youtube-data";
import tags from "../util/tags";
import "./index.scss";

const { CheckableTag } = Tag;

export default function Home() {
  const [channel, setChannel] = useState({});
  const [allTags, setAllTags] = useState(tags);
  const [selectedTags, setSelectedTags] = useState([]);
  const [showInput, setShowInput] = useState(false);
  const [inputValue, setInputValue] = useState("");

  const inputRef = useRef(null);

  useEffect(() => {
    // getChannel();
  }, []);

  async function getChannel() {
    let response = await youtubeDataService.getChannel();
    setChannel(response.data);
  }

  function handleChange(tag, checked) {
    const nextSelectedTags = checked
      ? [...selectedTags, tag]
      : selectedTags.filter((t) => t !== tag);
    setSelectedTags(nextSelectedTags);
  }

  function handleInputConfirm() {
    if (inputValue && tags.indexOf(inputValue) === -1) {
      setAllTags([...allTags, inputValue]);
    }
    setShowInput(false);
    setInputValue("");
  }

  const tagsComponent = allTags.map((tag, index) => (
    <CheckableTag
      key={tag + index}
      checked={selectedTags.indexOf(tag) > -1}
      onChange={(checked) => handleChange(tag, checked)}
    >
      {tag}
    </CheckableTag>
  ));

  const addNewTag = (
    <Tag
      className="site-tag-plus cursor-pointer"
      onClick={() => {
        setShowInput(true);
        setTimeout(() => {
          inputRef.current?.focus();
        }, 2000);
      }}
    >
      <PlusOutlined /> New Tag
    </Tag>
  );

  const channelLayout = (
    <Card className="channel-container">
      <div className="inner-container">
        <Image
          className="thumbnail"
          width={200}
          height={200}
          src="error"
          fallback="data:image/png;base64,"
        />
        <h2>Title</h2>
      </div>
      <h3>Tags</h3>
      <div className="inner-container">
        {tagsComponent}
        {!showInput && addNewTag}
        {showInput && (
          <Input
            // ref={inputRef}
            autoFocus
            type="text"
            size="small"
            className="tag-input"
            placeholder="Enter new tag"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onBlur={handleInputConfirm}
            onPressEnter={handleInputConfirm}
          />
        )}
      </div>
      <Button type="primary" onClick={getChannel} className="mx-auto d-block">
        Save
      </Button>
    </Card>
  );

  return (
    <div>
      <h1>{channel.title}</h1>
      {channelLayout}
    </div>
  );
}
